import React, { useEffect, useState } from "react";
import { db } from "../firebase";
import { collection, getDocs } from "firebase/firestore";
import Header from "../components/Header";

export default function AdminDashboard() {
  const [submissions, setSubmissions] = useState([]);
  const [loading, setLoading] = useState(true);

useEffect(() => {
  const fetchData = async () => {
    try {
      const querySnapshot = await getDocs(collection(db, "submissions"));
      console.log("Fetched docs:", querySnapshot.docs); // ðŸ‘ˆ add this line
      const results = querySnapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
      console.log("Mapped results:", results); // ðŸ‘ˆ add this too
      setSubmissions(results);
      setLoading(false);
    } catch (error) {
      console.error("Error fetching submissions:", error); // ðŸ‘ˆ catch fetch errors
      setLoading(false);
    }
  };

  fetchData();
}, []);

  return (
    <div className="min-h-screen bg-gray-100 text-gray-800">
      <Header />
      <div className="max-w-6xl mx-auto pt-32 px-4">
        <h1 className="text-3xl font-bold mb-6 text-center">Admin Dashboard</h1>
        {loading ? (
          <p className="text-center">Loading...</p>
        ) : submissions.length === 0 ? (
          <p className="text-center">No submissions found.</p>
        ) : (
          <div className="overflow-x-auto">
            <table className="min-w-full bg-white rounded-lg shadow-md">
              <thead>
                <tr className="bg-gray-200 text-gray-700 text-left">
                  <th className="py-2 px-4">Name</th>
                  <th className="py-2 px-4">Email</th>
                  <th className="py-2 px-4">Message</th>
                  <th className="py-2 px-4">Video Link</th>
                  <th className="py-2 px-4">Hourly Rate</th>
                </tr>
              </thead>
              <tbody>
                {submissions.map((sub) => (
                  <tr key={sub.id} className="border-t">
                    <td className="py-2 px-4 whitespace-nowrap">{sub.name}</td>
                    <td className="py-2 px-4 whitespace-nowrap">{sub.email}</td>
                    <td className="py-2 px-4 max-w-md truncate">{sub.message}</td>
                    <td className="py-2 px-4">
                      <a href={sub.videoLink} target="_blank" rel="noreferrer" className="text-blue-500 underline">
                        View Reel
                      </a>
                    </td>
                    <td className="py-2 px-4">${sub.rate}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>
    </div>
  );
}
